pragma solidity 0.8.4;

import "../interfaces/IPriceProxy.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

abstract contract AbstractPriceProxy is IPriceProxy, Ownable2Step {
    modifier nonZeroAddress(address _address) {
        require(_address != address(0), "PriceProxy: zero address");
        _;
    }

    modifier nonZeroFeedId(bytes32 _feedId) {
        require(_feedId != bytes32(0), "PriceProxy: zero feedId");
        _;
    }

    modifier nonEmptyPairName(string memory _pairName) {
        require(bytes(_pairName).length > 0, "PriceProxy: empty pair name");
        _;
    }

    address public override oracle;

    constructor(address _oracle) {
        _setOracle(_oracle);
    }

    function renounceOwnership() public virtual override onlyOwner {}

    /// @notice                     Sets oracle address
    /// @dev                        Only owner can call this
    /// @param _oracle              The address of the third-party oracle contract (e.g. Pyth or Switchboard)
    function setOracle(
        address _oracle
    ) external override onlyOwner {
        _setOracle(_oracle);
    }

    /// @notice                     Get the EMA price of the tokens by pair name
    /// @param _pairName            The price pair name (e.g. CORE/USDT  BTC/USDT)
    /// @return price               The EMA price of the price pair
    /// @return err                 Error message
    function getEmaPriceByPairName(
        string memory _pairName
    ) external override view returns(Price memory price, string memory err) {
        return _getEmaPriceByPairName(_pairName);
    }

    /// @notice                     Get the EMA prices of two tokens by their pair names
    /// @param _pairName0           The first price pair name (e.g. CORE/USDT  BTC/USDT)
    /// @param _pairName1           The second price pair name (e.g. CORE/USDT  BTC/USDT)
    /// @return price0              The EMA price of the first price pair
    /// @return price1              The EMA price of the second price pair
    /// @return err                 Error message
    function getEmaPricesByPairNames(
        string memory _pairName0,
        string memory _pairName1
    ) external override view returns(Price memory price0, Price memory price1, string memory err) {
        (price0, err) = _getEmaPriceByPairName(_pairName0);

        if (price0.price > 0) {
            (price1, err) = _getEmaPriceByPairName(_pairName1);
        }
    }


    /// @notice                     Sets oracle address
    /// @param _oracle              The address of the third-party oracle contract (e.g. Pyth or Switchboard)
    function _setOracle(
        address _oracle
    ) internal nonZeroAddress(_oracle) {
        require(oracle != _oracle, "PriceProxy: oracle already exists");
        require(_isOracleDeployed(_oracle), "PriceProxy: oracle contract not deployed");

        emit NewOracle(oracle, _oracle);

        oracle = _oracle;
    }

    /// @notice                     Gets the EMA price of the token by pair name
    /// @dev                        To be implemented by derived classes
    /// @param _pairName            The price pair name (e.g. CORE/USDT  BTC/USDT)
    /// @return price               The EMA price of the price pair
    /// @return err                 Error message
    function _getEmaPriceByPairName(
        string memory _pairName
    ) internal view virtual returns(Price memory price, string memory err);

    /// @notice                     Check whether the oracle contract has been deployed
    /// @param _oracle              The address of the third-party oracle contract (e.g. Pyth or Switchboard)
    /// @return                     Returns true if the oracle contract has been deployed
    function _isOracleDeployed(
        address _oracle
    ) private view returns(bool) {
        uint size;
        assembly{
            size := extcodesize(_oracle)
        }
        return size > 0;
    }

    /// @notice                     Converts a `bytes` to its ASCII `string` hexadecimal representation.
    function bytesToHex(
        bytes memory data
    ) internal pure returns (string memory) {
        bytes memory alphabet = "0123456789abcdef";
        bytes memory str = new bytes(2 * data.length + 2);

        str[0] = '0';
        str[1] = 'x';

        for (uint i = 0; i < data.length; i++) {
            str[2 * i + 2] = alphabet[uint8(data[i] >> 4)];
            str[2 * i + 3] = alphabet[uint8(data[i] & 0x0f)];
        }

        return string(str);
    }
}","keccak256":"0xf402ae52c6b442ea1a396f07e5929f7396df90cb790ad708b34f04abc9da4bde","license":"MIT"},"contracts/oracle/price-proxy-impl/SwitchboardPriceProxy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "./AbstractPriceProxy.sol";
import "../interfaces/ISwitchboardPush.sol";

import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract SwitchboardPriceProxy is AbstractPriceProxy {
    using SafeCast for int256;

    // Constants
    uint32 public constant SWITCHBOARD_VALUE_DECIMALS = 18;
    string public constant USDT_PRICE_PAIR_NAME = "USDT/USDT";

    constructor(address _oracle) AbstractPriceProxy(_oracle) {
    }

    /// @notice                     Gets the feed id of the token price by price pair name
    /// @dev                        The feed id is defined by the third-party oracle
    /// @param _pairName            The price pair name (e.g. CORE/USDT  BTC/USDT)
    /// @return feedId              The feed id of the token price
    function getFeedId(
        string memory _pairName
    ) external override pure returns(bytes32 feedId) {
        return _getFeedId(_pairName);
    }

    /// @notice                     Gets the EMA price of the token by pair name
    /// @param _pairName            The price pair name (e.g. CORE/USDT  BTC/USDT)
    /// @return price               The EMA price of the price pair
    /// @return err                 Error message
    function _getEmaPriceByPairName(
        string memory _pairName
    ) internal view override returns(Price memory price, string memory err) {
        if (Strings.equal(_pairName, USDT_PRICE_PAIR_NAME)) {
            price.price = 1;
            price.publishTime = block.timestamp;
            return (price, err);
        }

        // use latest price
        return _getLatestPriceByFeedId(_getFeedId(_pairName));
    }

    /// @notice                     Gets the latest price of the token by feed id
    /// @param _feedId              The feed id of the token price
    ///                             Defined by the third-party oracle
    /// @return price               The price of the price pair
    /// @return err                 Error message
    function _getLatestPriceByFeedId(
        bytes32 _feedId
    ) private nonZeroFeedId(_feedId) view returns(Price memory price, string memory err) {

        try ISwitchboardPush(oracle).feeds(_feedId) returns (ISwitchboardPush.Feed memory feed) {
            ISwitchboardPush.Result memory result = feed.latestResult;

            price.price = result.value.toUint256();
            price.decimals = SWITCHBOARD_VALUE_DECIMALS;
            price.publishTime = result.updatedAt;

        } catch Error(string memory _err) {
            err = _err;
        } catch(bytes memory _err) {
            err = bytesToHex(_err);
        }
    }

    /// @notice                     Gets the feed id of the token price by price pair name
    /// @dev                        The calculation rules of the feed id is defined by the third-party oracle
    /// @param _pairName            The price pair name (e.g. CORE/USDT  BTC/USDT)
    /// @return feedId              The feed id of the token price
    function _getFeedId(
        string memory _pairName
    ) private nonEmptyPairName(_pairName) pure returns (bytes32 feedId)  {
        assembly {
            feedId := mload(add(_pairName, 32))
        }
    }
}","keccak256":"0x7902ef9513c64ffa64efea9015f935ce90769a06b8a8934d9080e585f0f6aeea","license":"MIT"}},"version":1}
