        // solium-disable-previous-line security/no-inline-assembly
            ptr := mload(0x40) // load unused memory pointer
        }

        bytes29 _newView = unsafeJoin(memViews, ptr + 0x20);
        uint256 _written = len(_newView);
        uint256 _footprint = footprint(_newView);

        assembly {
        // solium-disable-previous-line security/no-inline-assembly
        // store the legnth
            mstore(ptr, _written)
        // new pointer is old + 0x20 + the footprint of the body
            mstore(0x40, add(add(ptr, _footprint), 0x20))
            ret := ptr
        }
    }
}","keccak256":"0xd3da86bebf2f55aed38f4d1fc84ac618bc94ab8951dea3d188b9a5e36f949c64","license":"MIT"},"contracts/common/relay/interfaces/IBitcoinRelay.sol":{"content":"// SPDX-License-Identifier: MIT
