pragma solidity 0.8.4;

import "./interfaces/ICollaterals.sol";
import "./interfaces/ILockers.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract CollateralsLogic is ICollaterals, Ownable2StepUpgradeable, UUPSUpgradeable {

    // *************** Modifiers ***************

    modifier nonZeroAddress(address _address) {
        require(_address != address(0), "Lockers: address is zero");
        _;
    }

    modifier nonZeroAmount(uint _amount) {
        require(_amount > 0, "Lockers: amount is zero");
        _;
    }

    // Constants
    uint public constant NATIVE_TOKEN_DECIMAL = 18;
    address public constant NATIVE_TOKEN = address(1);

    // Public variables
    address public override lockers;
    mapping(address => uint) public collateralsMap;
    collateral[] public availableCollaterals;

    constructor() {
        _disableInitializers();
    }

    /// @notice                             Gives default params to initiate collaterals
    /// @param _lockers                     Address of lockers contract
    /// @param _minRequiredCORELockedAmount Minimum required locked amount of native token
    function initialize(
        address _lockers,
        uint _minRequiredCORELockedAmount
    ) public initializer {

        Ownable2StepUpgradeable.__Ownable2Step_init();
        UUPSUpgradeable.__UUPSUpgradeable_init();

        _setLockers(_lockers);
        _addCollateral(NATIVE_TOKEN, _minRequiredCORELockedAmount);
    }

    function renounceOwnership() public virtual override onlyOwner {}

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    // *************** External functions ***************

    /**
     * @dev Gets the total number of supported collateral types
     */
    function getTotalNumber() external view override returns (uint) {
        return availableCollaterals.length;
    }

    /// @notice                     Gets the decimals of collateral
    /// @dev                        The decimals of native token is 18
    ///                             The decimals of ERC-20 token needs to be obtained by calling the decimals method
    /// @param _token               The address of collateral
    /// @return                     The decimals of collateral
    function getDecimals(address _token) external view override returns (uint) {
        checkSupported(_token);

        if (_token == NATIVE_TOKEN) {
            return NATIVE_TOKEN_DECIMAL;
        }

        return ERC20(_token).decimals();
    }

    /// @notice                     Gets the minimum required locked amount of the collateral
    /// @param _token               The address of collateral
    /// @return                     The minimum required locked amount of the token
    function getMinLockedAmount(address _token) public view override returns (uint) {
        checkSupported(_token);

        uint index = collateralsMap[_token] - 1;
        return availableCollaterals[index].minLockedAmount;
    }

    /// @dev Gets collateral information stored in the list
    function getCollateral(uint index) external view override returns (collateral memory) {
        require(index < availableCollaterals.length, "Lockers: index out of bound");
        return availableCollaterals[index];
    }

    /// @notice                     Checks if the locked amount is valid
    /// @param _token               The address of collateral
    /// @param _lockedAmount        The amount of collateral intended for locking
    function checkLockedAmount(
        address _token,
        uint _lockedAmount
    ) external view override {
        uint minLockedAmount = getMinLockedAmount(_token);

        if (_lockedAmount < minLockedAmount) {
            revert InsufficientCollateral(_token, _lockedAmount, minLockedAmount);
        }
    }

    /// @notice                     Sets address of lockers contract
    /// @param _lockers             The address of lockers contract
    function setLockers(address _lockers) external override onlyOwner {
        _setLockers(_lockers);
    }

    /// @notice                     Sets minimum required locked amount of collateral
    /// @param _token               The address of collateral
    /// @param _minLockedAmount     The minimum required locked amount of collateral
    function setMinLockedAmount(
        address _token,
        uint _minLockedAmount
    ) external override onlyOwner {
        _setMinLockedAmount(_token, _minLockedAmount);
    }

    /// @notice                     Adds a collateral to available list
    /// @param _token               The address of collateral
    /// @param _minLockedAmount     The minimum required locked amount of collateral
    function addCollateral(
        address _token,
        uint _minLockedAmount
    ) external override onlyOwner {
        _addCollateral(_token, _minLockedAmount);
    }

    /// @notice                     Removes a collateral from available list
    /// @dev                        Only Lockers contract can call this
    /// @param _token               The address of collateral
    function removeCollateral(address _token) external override onlyOwner {
        _removeCollateral(_token);
    }

    // *************** Internal functions ***************

    /// @notice                     Checks whether a token is supported as collateral
    /// @param _token               The address of collateral
    function checkSupported(address _token) internal view nonZeroAddress(_token) {
        require(collateralsMap[_token] > 0, "Lockers: unsupported collateral");
    }

    /// @notice                     Checks whether a token is not supported as collateral
    /// @param _token               The address of collateral
    function checkUnsupported(address _token) internal view nonZeroAddress(_token) {
        require(collateralsMap[_token] == 0, "Lockers: supported collateral");
    }

    function _setLockers(address _lockers) internal nonZeroAddress(_lockers) {
        emit NewLockers(lockers, _lockers);
        lockers = _lockers;
    }

    function _setMinLockedAmount(
        address _token,
        uint _minLockedAmount
    ) internal nonZeroAmount(_minLockedAmount) {
        checkSupported(_token);

        uint index = collateralsMap[_token] - 1;
        collateral storage item = availableCollaterals[index];

        emit NewMinRequiredLockedAmount(_token, item.minLockedAmount, _minLockedAmount);
        item.minLockedAmount = _minLockedAmount;
    }

    function _addCollateral(
        address _token,
        uint _minLockedAmount
    ) internal nonZeroAmount(_minLockedAmount) {
        checkUnsupported(_token);

        collateral memory item = collateral(_token, _minLockedAmount);
        availableCollaterals.push(item);

        collateralsMap[_token] = availableCollaterals.length;

        emit NewSupportedCollateral(_token, _minLockedAmount);
    }

    function _removeCollateral(address _token) internal nonZeroAddress(_token) {
        checkSupported(_token);

        require(
            ILockers(lockers).isCollateralUnused(_token),
            "Lockers: collateral in use"
        );

        uint index = collateralsMap[_token] - 1;
        uint lastIndex = availableCollaterals.length - 1;
        if (index < lastIndex) {
            availableCollaterals[index] = availableCollaterals[lastIndex];
            collateralsMap[availableCollaterals[index].token] = index + 1;
        }

        availableCollaterals.pop();
        delete collateralsMap[_token];

        emit RevokeSupportedCollateral(_token);
    }
}","keccak256":"0xf23c8cb3d921d8512f69c85c71b7de22886b8cc56f65e558b3ee3668ffc95ece","license":"MIT"},"contracts/lockers/interfaces/ICollaterals.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "../../types/DataTypes.sol";

interface ICollaterals {
    // struct
    struct collateral {
        address token;
        uint minLockedAmount;
    }

    // Events
    event NewMinRequiredLockedAmount(
        address indexed token,
        uint oldMinRequiredLockedAmount,
        uint newMinRequiredLockedAmount
    );

    event NewLockers(address oldLockers, address newLockers);

    event NewSupportedCollateral(address indexed token, uint minLockedAmount);

    event RevokeSupportedCollateral(address indexed token);

    // Errors
    error InsufficientCollateral(address collateralToken, uint lockedAmount, uint minLockedAmount);

    // Read-only functions
    function lockers() external view returns (address);

    function getTotalNumber() external view returns (uint);

    function getDecimals(address _token) external view returns (uint);

    function getMinLockedAmount(address _token) external view returns (uint);

    function getCollateral(uint _index) external view returns (collateral memory);

    function checkLockedAmount(address _token, uint _lockedAmount) external view;


    // State-changing functions
    function setLockers(address _lockers) external;

    function setMinLockedAmount(address _token, uint _minLockedAmount) external;

    function addCollateral(address _token, uint _minLockedAmount) external;

    function removeCollateral(address _token) external;
}","keccak256":"0x3d29de86c37a78285d48444e6a3f6276eaca56def2bc55d67bfd7e7c36b0338d","license":"MIT"},"contracts/lockers/interfaces/ILockers.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "./ILockersStorage.sol";

interface ILockers is ILockersStorage {

     // Events

    event RequestAddLocker(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint lockedAmount,
        address lockedToken
    );

    event RevokeAddLockerRequest(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint lockedAmount,
        address lockedToken
    );

    event RequestInactivateLocker(
        address indexed lockerTargetAddress,
        uint indexed inactivationTimestamp,
        bytes lockerLockingScript,
        uint lockedAmount,
        uint netMinted,
        address lockedToken
    );

    event ActivateLocker(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint lockedAmount,
        uint netMinted,
        address lockedToken
    );

    event LockerAdded(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint lockedAmount,
        uint addingTime,
        address lockedToken
    );

    event LockerRemoved(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint unlockedAmount,
        address unlockedToken
    );

    event LockerSlashed(
        address indexed lockerTargetAddress,
        uint rewardAmount,
        address indexed rewardRecipient,
        uint amount,
        address indexed recipient,
        uint slashedCollateralAmount,
        uint slashTime,
        bool isForCCBurn,
        address slashedCollateralToken
    );

    event LockerLiquidated(
        address indexed lockerTargetAddress,
        address indexed liquidatorAddress,
        uint collateralAmount,
        uint coreBTCAmount,
        uint liquidateTime,
        address collateralToken
    );

    event LockerSlashedCollateralSold(
        address indexed lockerTargetAddress,
        address indexed buyerAddress,
        uint slashingAmount,
        uint coreBTCAmount,
        uint slashingTime,
        address slashingToken
    );

    event CollateralAdded(
        address indexed lockerTargetAddress,
        uint addedCollateral,
        uint totalCollateral,
        uint addingTime,
        address collateralToken
    );

    event CollateralRemoved(
        address indexed lockerTargetAddress,
        uint removedCollateral,
        uint totalCollateral,
        uint removingTime,
        address collateralToken
    );

    event MintByLocker(
        address indexed lockerTargetAddress,
        address indexed receiver,
        bytes32 bitcoinTxId,
        uint mintedAmount,
        uint lockerFee,
        uint mintingTime
    );

    event BurnByLocker(
        address indexed lockerTargetAddress,
        uint burntAmount,
        uint lockerFee,
        uint burningTime
    );

    event MinterAdded(
        address indexed account
    );

    event MinterRemoved(
        address indexed account
    );

    event BurnerAdded(
        address indexed account
    );

    event BurnerRemoved(
        address indexed account
    );

    event NewLockerPercentageFee(
        uint oldLockerPercentageFee,
        uint newLockerPercentageFee
    );

    event NewPriceWithDiscountRatio(
        uint oldPriceWithDiscountRatio,
        uint newPriceWithDiscountRatio
    );

    event NewPriceOracle(
        address oldPriceOracle,
        address newPriceOracle
    );

    event NewCCBurnRouter(
        address oldCCBurnRouter,
        address newCCBurnRouter
    );

    event NewCoreBTC(
        address oldCoreBTC,
        address newCoreBTC
    );

    event NewCollateralRatio(
        uint oldCollateralRatio,
        uint newCollateralRatio
    );

    event NewLiquidationRatio(
        uint oldLiquidationRatio,
        uint newLiquidationRatio
    );

    event NewSlashCompensationRatio(
        uint oldSlashCompensationRatio,
        uint newSlashCompensationRatio
    );

    event NewCollaterals(
        address oldCollaterals,
        address newCollaterals
    );

    // Read-only functions

    function getLockerTargetAddress(bytes calldata _lockerLockingScript) external view returns (address);

    function isLocker(bytes calldata _lockerLockingScript) external view returns (bool);

    function getLockerLockingScript(address _lockerTargetAddress) external view returns (bytes memory);

    function isLockerActive(address _lockerTargetAddress) external view returns (bool);

    function getLockerCapacity(address _lockerTargetAddress) external view returns (uint);

    function priceOfOneUnitOfCollateralInBTC(address _collateralToken) external view returns (uint);

    function isMinter(address account) external view returns(bool);

    function isBurner(address account) external view returns(bool);

    function getHealthFactor(address _lockerTargetAddress) external view returns(uint);

    function getMaximumBuyableCollateral(address _lockerTargetAddress) external view returns (uint);

    function getNeededCoreBTCToBuyCollateral(
        address _lockerTargetAddress,
        uint _collateralAmount
    ) external view returns(uint);

    function candidateLockers(uint index) external view returns(address);

    function isCollateralUnused(address _token) external view returns (bool);

    // State-changing functions

    function pauseLocker() external;

    function unPauseLocker() external;

    function addMinter(address _account) external;

    function removeMinter(address _account) external;

    function addBurner(address _account) external;

    function removeBurner(address _account) external;

    function mint(bytes calldata _lockerLockingScript, address _receiver, bytes32 _txId, uint _amount) external returns(uint);

    function burn(bytes calldata _lockerLockingScript, uint256 _amount) external returns(uint);

    function setLockerPercentageFee(uint _lockerPercentageFee) external;

    function setPriceWithDiscountRatio(uint _priceWithDiscountRatio) external;

    function setPriceOracle(address _priceOracle) external;

    function setCCBurnRouter(address _ccBurnRouter) external;

    function setCoreBTC(address _coreBTC) external;

    function setCollateralRatio(uint _collateralRatio) external;

    function setLiquidationRatio(uint _liquidationRatio) external;

    function setSlashCompensationRatio(uint _slashCompensationRatio) external;

    function setCollaterals(address _collaterals) external;

    function liquidateLocker(
        address _lockerTargetAddress,
        uint _btcAmount
    ) external returns (bool);

    function addCollateral(
        address _lockerTargetAddress,
        uint _addingCollateralAmount
    ) external payable returns (bool);

    function removeCollateral(
        uint _removingCollateralAmount
    ) external payable returns (bool);

    function requestToBecomeLocker(
        bytes calldata _lockerLockingScript,
        uint _lockedAmount,
        ScriptTypes _lockerRescueType,
        bytes calldata _lockerRescueScript,
        address _lockedToken
    ) external payable returns (bool);

    function revokeRequest() external returns (bool);

    function addLocker(address _lockerTargetAddress) external returns (bool);

    function requestInactivation() external returns (bool);

    function requestActivation() external returns (bool);

    function selfRemoveLocker() external returns (bool);

    function slashIdleLocker(
        address _lockerTargetAddress,
        uint _rewardAmount,
        address _rewardRecipient,
        uint _amount,
        address _recipient
    ) external returns(bool);

    function slashThiefLocker(
        address _lockerTargetAddress,
        uint _rewardAmount,
        address _rewardRecipient,
        uint _amount
    ) external returns(bool);

    function buySlashedCollateralOfLocker(
        address _lockerTargetAddress,
        uint _collateralAmount
    ) external returns (bool);
}","keccak256":"0xb54a1c855c3ab4d4e5f888a5b815821dd5320db2ffc33d1058bf83a7dfd7bca3","license":"MIT"},"contracts/lockers/interfaces/ILockersStorage.sol":{"content":"// SPDX-License-Identifier: MIT
